import React, { useMemo, useState } from "react";
import ProductList from "./ProductList";

const ShoppingCart = () => {
  const [cart, setCart] = useState([
    { id: 1, name: "Product A", price: 100 },
    { id: 2, name: "Product B", price: 200 },
    { id: 3, name: "Product C", price: 300 },
  ]);

  const [discount, setDiscount] = useState(0);
  const [newProductName, setNewProductName] = useState("");
  const [newProductPrice, setNewProductPrice] = useState("");

  const total = useMemo(() => {
    return cart.reduce((sum, product) => sum + product.price, 0) - discount;
  }, [cart, discount]);

  const addProduct = () => {
    if (!newProductName || !newProductPrice || isNaN(newProductPrice)) {
      alert("Please enter valid product name and price.");
      return;
    }

    const newProductId = cart.length + 1;
    const newProduct = {
      id: newProductId,
      name: newProductName,
      price: parseFloat(newProductPrice),
    };

    setCart((prevCart) => [...prevCart, newProduct]);
    setNewProductName(""); // Clear input fields
    setNewProductPrice("");
  };

  return (
    <div>
      <h1>Shopping Cart</h1>
      <ProductList products={cart} />
      <div style={{ marginTop: "20px" }}>
        <label>
          Discount:
          <input
            type="number"
            value={discount}
            onChange={(e) => setDiscount(Number(e.target.value))}
          />
        </label>
        <p>Total: ${total}</p>
      </div>
      <div style={{ marginTop: "20px" }}>
        <h3>Add New Product</h3>
        <input
          type="text"
          placeholder="Product Name"
          value={newProductName}
          onChange={(e) => setNewProductName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Product Price"
          value={newProductPrice}
          onChange={(e) => setNewProductPrice(e.target.value)}
        />
        <button onClick={addProduct} style={{ marginTop: "10px" }}>
          Add Product
        </button>
      </div>
    </div>
  );
};

export default ShoppingCart;
